import React from 'react';
import './Results.css';

interface ResultsProps {
  wpm: number;
  accuracy: number;
  timeElapsed: number;
}

const Results: React.FC<ResultsProps> = ({ wpm, accuracy, timeElapsed }) => {
  // Format time in minutes and seconds
  const formatTime = (seconds: number): string => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins}:${secs < 10 ? '0' : ''}${secs}`;
  };

  // Calculate a score based on WPM and accuracy
  const calculateScore = (): number => {
    // Base score from WPM
    const wpmFactor = wpm * 50;

    // Accuracy modifier (0.5 at 80% to 1.2 at 100%)
    const accuracyModifier = Math.max(0.5, (accuracy / 100) * 1.5 - 0.3);

    // Final score calculation
    return Math.round(wpmFactor * accuracyModifier);
  };

  const score = calculateScore();
  const scoreMessage = score => {
    if (score >= 5000) return "Excellent!";
    if (score >= 3000) return "Great job!";
    if (score >= 2000) return "Good work!";
    return "Keep practicing!";
  };

  return (
    <div className="results-container">
      <h2>Race Complete!</h2>

      <div className="results-stats">
        <div className="result-item">
          <span className="result-label">Words Per Minute:</span>
          <span className="result-value">{wpm}</span>
        </div>

        <div className="result-item">
          <span className="result-label">Accuracy:</span>
          <span className="result-value">{accuracy}%</span>
        </div>

        <div className="result-item">
          <span className="result-label">Time:</span>
          <span className="result-value">{formatTime(timeElapsed)}</span>
        </div>

        <div className="result-item score">
          <span className="result-label">Score:</span>
          <span className="result-value">{score}</span>
        </div>
      </div>

      <div className="results-message">
        {scoreMessage(score)}
      </div>
    </div>
  );
};

export default Results;
