import React from 'react';
import styled from 'styled-components';

interface RaceTrackProps {
  position: number; // 0-100 percentage
}

const TrackContainer = styled.div`
  width: 100%;
  height: 80px;
  background-color: #f0f0f0;
  border-radius: 40px;
  position: relative;
  overflow: hidden;
  margin-bottom: 20px;
  border: 2px solid #ddd;
`;

const TrackRoad = styled.div`
  width: 100%;
  height: 40px;
  background-color: #555;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);

  &::before {
    content: '';
    position: absolute;
    height: 6px;
    width: 100%;
    background: repeating-linear-gradient(
      to right,
      #fff,
      #fff 20px,
      transparent 20px,
      transparent 40px
    );
    top: 50%;
    transform: translateY(-50%);
  }
`;

const FinishLine = styled.div`
  position: absolute;
  right: 10px;
  height: 60px;
  width: 10px;
  background: repeating-linear-gradient(
    to bottom,
    black,
    black 10px,
    white 10px,
    white 20px
  );
  top: 50%;
  transform: translateY(-50%);
  z-index: 1;
`;

const Car = styled.div<{ position: number }>`
  width: 60px;
  height: 30px;
  background-color: #e74c3c;
  border-radius: 5px;
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  left: ${props => props.position}%;
  transition: left 0.3s ease-out;
  z-index: 2;

  &::before {
    content: '';
    position: absolute;
    width: 10px;
    height: 10px;
    background-color: #333;
    border-radius: 50%;
    bottom: -5px;
    left: 10px;
  }

  &::after {
    content: '';
    position: absolute;
    width: 10px;
    height: 10px;
    background-color: #333;
    border-radius: 50%;
    bottom: -5px;
    right: 10px;
  }
`;

const RaceTrack: React.FC<RaceTrackProps> = ({ position }) => {
  const carPosition = position > 0 ? position - 5 : 0; // Adjust for car width

  return (
    <TrackContainer>
      <TrackRoad />
      <Car position={carPosition} />
      <FinishLine />
    </TrackContainer>
  );
};

export default RaceTrack;
