import React from 'react';
import styled from 'styled-components';
import { Difficulty, TimeSetting } from '../App';

interface GameSettingsProps {
  difficulty: Difficulty;
  setDifficulty: (difficulty: Difficulty) => void;
  timeSetting: TimeSetting;
  setTimeSetting: (time: TimeSetting) => void;
  onStartGame: () => void;
}

const SettingsContainer = styled.div`
  background-color: white;
  border-radius: 10px;
  padding: 30px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  max-width: 600px;
  margin: 0 auto;
`;

const SettingsTitle = styled.h2`
  text-align: center;
  margin-bottom: 20px;
  color: #333;
`;

const SettingsGroup = styled.div`
  margin-bottom: 20px;
`;

const SettingsLabel = styled.h3`
  margin-bottom: 10px;
  color: #444;
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
  margin-bottom: 20px;
`;

const Button = styled.button<{ active?: boolean }>`
  padding: 10px 15px;
  border: 2px solid #3498db;
  border-radius: 5px;
  background-color: ${props => props.active ? '#3498db' : 'white'};
  color: ${props => props.active ? 'white' : '#3498db'};
  font-weight: bold;
  cursor: pointer;
  transition: all 0.2s;
  flex: 1;

  &:hover {
    background-color: ${props => props.active ? '#2980b9' : '#f1f9ff'};
  }
`;

const StartButton = styled.button`
  display: block;
  width: 100%;
  padding: 15px;
  background-color: #2ecc71;
  color: white;
  border: none;
  border-radius: 5px;
  font-size: 1.2rem;
  font-weight: bold;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background-color: #27ae60;
  }
`;

const DifficultyDescription = styled.p`
  margin-top: 10px;
  color: #666;
  font-style: italic;
`;

const GameSettings: React.FC<GameSettingsProps> = ({
  difficulty,
  setDifficulty,
  timeSetting,
  setTimeSetting,
  onStartGame
}) => {
  const getDifficultyDescription = () => {
    switch (difficulty) {
      case 'easy':
        return 'Simple vocabulary and common sentences - perfect for beginners.';
      case 'medium':
        return 'More complex vocabulary and sentence structures for intermediate typists.';
      case 'hard':
        return 'Advanced vocabulary, technical terms, and complex sentence structures for expert typists.';
      default:
        return '';
    }
  };

  return (
    <SettingsContainer>
      <SettingsTitle>Game Settings</SettingsTitle>

      <SettingsGroup>
        <SettingsLabel>Difficulty Level</SettingsLabel>
        <ButtonGroup>
          <Button 
            active={difficulty === 'easy'} 
            onClick={() => setDifficulty('easy')}
          >
            Easy
          </Button>
          <Button 
            active={difficulty === 'medium'} 
            onClick={() => setDifficulty('medium')}
          >
            Medium
          </Button>
          <Button 
            active={difficulty === 'hard'} 
            onClick={() => setDifficulty('hard')}
          >
            Hard
          </Button>
        </ButtonGroup>
        <DifficultyDescription>{getDifficultyDescription()}</DifficultyDescription>
      </SettingsGroup>

      <SettingsGroup>
        <SettingsLabel>Time Setting</SettingsLabel>
        <ButtonGroup>
          <Button 
            active={timeSetting === 30} 
            onClick={() => setTimeSetting(30)}
          >
            30 Seconds
          </Button>
          <Button 
            active={timeSetting === 60} 
            onClick={() => setTimeSetting(60)}
          >
            1 Minute
          </Button>
          <Button 
            active={timeSetting === 120} 
            onClick={() => setTimeSetting(120)}
          >
            2 Minutes
          </Button>
        </ButtonGroup>
      </SettingsGroup>

      <StartButton onClick={onStartGame}>Start Typing!</StartButton>
    </SettingsContainer>
  );
};

export default GameSettings;
