import React from 'react';
import './GameSettings.css';

interface GameSettingsProps {
  difficulty: string;
  onDifficultyChange: (difficulty: string) => void;
  onStartGame: () => void;
}

const GameSettings: React.FC<GameSettingsProps> = ({
  difficulty,
  onDifficultyChange,
  onStartGame
}) => {
  return (
    <div className="game-settings">
      <h2>Game Settings</h2>

      <div className="settings-container">
        <div className="setting-group">
          <h3>Select Difficulty</h3>
          <div className="difficulty-options">
            <button
              className={`difficulty-option ${difficulty === 'easy' ? 'selected' : ''}`}
              onClick={() => onDifficultyChange('easy')}
            >
              <span className="difficulty-icon">üü¢</span>
              <span className="difficulty-name">Easy</span>
              <span className="difficulty-desc">100 words - Short, simple sentences</span>
            </button>

            <button
              className={`difficulty-option ${difficulty === 'medium' ? 'selected' : ''}`}
              onClick={() => onDifficultyChange('medium')}
            >
              <span className="difficulty-icon">üü°</span>
              <span className="difficulty-name">Medium</span>
              <span className="difficulty-desc">150 words - Moderate vocabulary</span>
            </button>

            <button
              className={`difficulty-option ${difficulty === 'hard' ? 'selected' : ''}`}
              onClick={() => onDifficultyChange('hard')}
            >
              <span className="difficulty-icon">üî¥</span>
              <span className="difficulty-name">Hard</span>
              <span className="difficulty-desc">200 words - Complex topics</span>
            </button>
          </div>
        </div>

        <div className="how-to-play">
          <h3>How to Play</h3>
          <ul>
            <li>Type the text displayed as accurately and quickly as possible</li>
            <li>Your car moves based on your progress through the text</li>
            <li>Race to the finish line by completing the target word count</li>
            <li>Your final score is based on typing speed (WPM) and accuracy</li>
          </ul>
        </div>
      </div>

      <button className="start-game-button" onClick={onStartGame}>
        Start Race! üèÅ
      </button>
    </div>
  );
};

export default GameSettings;
